INSERT INTO `biz_hospital` VALUES ('051204121ddf4205af5040e6851260cd', '四川博科斯电力科技有限公司', '工业', '否', '', '', '2014-01-02', '2500', '吴杰', '杜丽红', '13518119520', '18280477500', '成都市双流区经开区空港四路2800号', '91510122086657627J', '10', '', '<p style=\"background:white;\">\r\n	<span>&nbsp;</span>四川博科斯电力科技有限公司是一家集研发、生产、销售为一体的高新技术企业。公司主要致力于研发生产，符合智能电网发展要求的，新型智能配电设备和新型环保材质配电设备。公司成立于<span>2014</span>年<span>1</span>月，位于四川省成都市天府新区西南航空港经济开发区工业集中发展区空港四路，占地面积<span>32300</span>平方米，公司注册资本<span>2500</span>万元，公司现有员工<span>140</span>多人，其中高级技术员工<span>43</span>人。现有设备<span>50</span>余台，调试检测设备<span>20</span>余台，已先后在北京、重庆、云南、贵州、甘肃、西藏等地设立分支机构及办事处。公司具备完善先进的加工设备、具有过硬的装配技术和先进的检测设备。公司已通过国家质量认证中心的<span>ISO9001</span>质量体系认证、<span>ISO14001</span>环境管理体系认证和<span>ISO18001</span>职业健康管理体系认证。\r\n</p>\r\n<p style=\"background:white;\">\r\n	&nbsp;&nbsp;公司主要经营：<span>SMC</span>智能环保型电能计量箱、<span>SMC</span>智能环保型电力综合配电柜（<span>SMC/JP</span>柜）、<span>SMC</span>玻璃钢配电箱，<span>SMC</span>玻璃钢电缆分接箱、户外开闭所、固体绝缘环网柜、全封闭式<span>SF6</span>环网柜、美式（欧式）箱式变电站、各种高低压成套设备等。公司产品广泛应用于国家电网公司、南方电网公司以及各地方电网公司、城网、农网智能户表改造工程，<span>110KV</span>及以下变电站配电新建工程和配网改造工程；目前公司研发研发产品已取得多项专利。\r\n</p>\r\n<p style=\"background:white;\">\r\n	&nbsp;&nbsp;&nbsp;我们依托自身专业化的视野和高标准的研发团队，结合行业前沿技术要求和最流行的现代设计理念，并集多年丰富的行业运作经验，形成了自成一体的风格；凭借对市场趋势的敏锐洞察和客户需要的深刻理解，深入到客户的企业内部进行全方位、多角度的了解，反复沟通把脉，再结合市场需求和行业特征进行全盘的整理、揉合、策划、直至执行生产，最终达到顾客满意。本公司遵循“质量为优，服务为杰”的宗旨将为您提供最优质的产品和最满意的售后服务，竭诚欢迎新老客户莅临指导，洽谈业务，愿与各届同仁通力合作，共同开创事业的新天地。\r\n</p>\r\n<p style=\"background:white;\">\r\n	<b>我们的理念：质量为重、顾客至上</b>\r\n</p>\r\n<b>我们的方针：用心创造、诚信经营、共同发展</b>', null, null, null, null, '0.00', '0.00', null, null, '0', null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null);
