INSERT INTO `biz_hospital` VALUES ('266f057c8d9d4785b190ed1ff195d121', '四川云盾光电科技有限公司', '先进制造业', '是', '2015-10-09', '2018-10-08', '2013-10-24', '510', '张宜文', '姜莉', '13808173224', '15182861551', '四川省成都市双流区西南航空港经济开发区工业集中区三期', '91510122080640816A', '16', '16', '<p class=\"MsoNormal\" style=\"text-indent:28.0pt;\">\r\n	<span style=\"font-size:14.0pt;font-family:仿宋_GB2312;\"><span style=\"font-size:16px;\">四川云盾光电科技有限公司</span><span style=\"color:black;font-size:16px;\">位于成都双流西南航空港经济开发区工业集中区三期内。公司</span><span style=\"font-size:16px;\">以中国科学院相关院所、中国兵器工业集团等单位的相关技术及成果为依托，主要从事光电类高新技术产品的研发、生产、销售及服务，以及科研成果的推广应用及转移、转化。</span><span></span></span>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:28.0pt;\">\r\n	<span style=\"font-size:14.0pt;font-family:仿宋_GB2312;\"><span style=\"font-size:16px;\">研究范围涉及</span><span style=\"color:black;font-size:16px;\">光学工程设计</span><span style=\"font-size:16px;\">、微光学系统设计、</span><span style=\"color:black;font-size:16px;\">光电检测仪器开发、民用光电产品、</span><span style=\"font-size:16px;\">光电成像技术、</span><span style=\"color:black;\"><span style=\"font-size:16px;\">计算机软硬件、安防工程、建筑智能化工程、消防工程、精密机械结构设计等高新技术领域。</span><span></span></span></span>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:28.0pt;\">\r\n	<span style=\"font-size:16px;font-family:仿宋_GB2312;color:black;\">公司产品主要有微光学防伪标识、微光学系统及器件、光电检测仪器、高清智能监控系统、红外光电成像系统、各类光学望远镜等；并承接各类光学设计，光学器件加工，精密机械结构设计、制造，电子技术及计算机软件开发工程。</span><span style=\"font-size:14.0pt;font-family:仿宋_GB2312;\"></span>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:28.0pt;\">\r\n	<span style=\"font-size:14.0pt;font-family:仿宋_GB2312;\"><span style=\"font-size:16px;\">公司在光学、机械、电子及计算机技术等领域具有较强的研究、开发和生产实力，现有</span><span style=\"color:black;\"><span style=\"font-size:16px;\">员工</span><span style=\"font-size:16px;\">20</span><span style=\"font-size:16px;\">余人，其中高级技术职称人员</span><span style=\"font-size:16px;\">6</span><span style=\"font-size:16px;\">人，中级技术职称人员</span><span style=\"font-size:16px;\">8</span><span style=\"font-size:16px;\">人。目前公司主要有</span></span><b><span style=\"color:red;font-size:16px;\">光学及微光学工程</span></b><span style=\"color:black;font-size:16px;\">、</span><b><span style=\"color:red;font-size:16px;\">光电检测仪器</span></b><span style=\"color:black;font-size:16px;\">、</span><b><span style=\"color:red;font-size:16px;\">智能化建筑工程</span></b><span style=\"color:black;font-size:16px;\">、</span><b><span style=\"color:red;font-size:16px;\">民用光电产品</span></b><span style=\"color:black;font-size:16px;\">及</span><b><span style=\"color:red;font-size:16px;\">红外光电成像系统</span></b><span style=\"color:black;\"><span style=\"font-size:16px;\">五个主要业务方向。</span><span></span></span></span>\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:16px;font-family:华文仿宋;color:black;\">&nbsp;&nbsp;</span><span style=\"font-size:16px;font-family:仿宋_GB2312;color:black;\"><span style=\"font-size:16px;\">&nbsp; </span></span><span style=\"font-size:14.0pt;font-family:仿宋_GB2312;color:black;\"><span style=\"font-size:16px;\">公司以“创新成果产业化”为宗旨，坚持以市场为导向、质量为基础、人才为支撑，与科研机构密切合作，不断研发具有较好市场前景与核心技术的光机电产品，努力成为国内一流、国际知名的光机电产品研发、生产、销</span><span style=\"font-size:16px;\">售及服务企业。</span></span><b><span style=\"font-size:14.0pt;font-family:仿宋_GB2312;color:black;\"> <span></span></span></b>\r\n</p>', null, null, null, null, '0.00', '0.00', null, null, '0', null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null);
