INSERT INTO `biz_hospital` VALUES ('2ea4ed19c98d45aa99f3af0d4fd4646e', '四川华神兽用生物制品有限公司', '生物医药', '否', '2008-12-30', '2011-12-30', '2007-02-07', '5000', '邝声耀', '郭沛', '85895885', '13688364088', '双流西南航空港经济开发区工业集中区内', '91510122797822473H', '100', '19', '<span> </span> \r\n<p style=\"text-indent:21pt;\" class=\"MsoNormal\">\r\n	<span><span style=\"color:black;line-height:150%;font-family:宋体;\">四川华神兽用生物制品有限公司是四川省畜牧科学研究院（畜科公司）全资子公司，筹建于<span>2005</span>年，致力于新型动物疫苗的研发、生产、销售，分别于<span>2006</span>年<span>3</span>月<span>2011</span>年<span>4</span>月<span>2</span>次通过农业部<span>GMP</span>认证及复验。并于<span>2008</span>年、<span>2011</span>年连续两次通过成都市高新技术企业认证。</span><span style=\"line-height:150%;font-family:宋体;\">控股单位四川省畜牧科学研究院是一所具有<span>70</span>余年悠久历史的公益性研究机构，是国内一流的区域性畜牧科技创新中心和人才培养基地。研究院承担国家重大科技支撑计划、<span>863</span>计划、国家自然科学基金、国家农业产业技术体系、省畜禽育种攻关等基础和应用研究课题，在遗传育种、生物技术、饲料营养、疫病防控、健康养殖、生产系统等领域开展畜牧兽医新技术、新产品研究，培养畜牧兽医技术人才，推广现代畜牧业生产技术，为四川及西南地区现代畜牧业发展提供科技支撑。<span></span></span></span> \r\n</p>\r\n<span> </span> \r\n<p style=\"text-indent:21pt;\" class=\"MsoNormal\">\r\n	<span><span style=\"line-height:150%;font-family:宋体;\">公司</span><span style=\"color:black;line-height:150%;font-family:宋体;\">依托四川省畜牧科学研究院坚实的研发实力，与国内多个高等院校建立良好的合作关系，其中与四川农业大学共同研制开发的产品猪伪狂犬病活疫苗（<span>SA215</span>株）获得农业部颁发的二类新兽药证书，成为我国第一株从技术上达到国际疫苗应用标准、完全自主知识产权的动物病毒基因工程疫苗。</span><span style=\"line-height:150%;font-family:宋体;\"><span> </span></span></span> \r\n</p>\r\n<span> </span> \r\n<p style=\"text-indent:21pt;\" class=\"MsoNormal\">\r\n	<span><span style=\"line-height:150%;font-family:宋体;\">公司积极响应政府号召，于<span>2013</span>年整体搬迁至成都双流航空港经济开发区，已征地<span>80</span></span><span style=\"line-height:150%;font-family:宋体;\">亩，新厂投资规模预计</span><span style=\"line-height:150%;\"><span>2.4</span></span><span style=\"line-height:150%;font-family:宋体;\">亿元，建筑面积达</span><span style=\"line-height:150%;\"><span>4</span></span><span style=\"line-height:150%;font-family:宋体;\">万平方米，第一期已经完成投资</span><span style=\"line-height:150%;\"><span>1.5</span></span><span style=\"line-height:150%;font-family:宋体;\">亿元，引进国内先进的生物疫苗生产设备，</span><span style=\"line-height:150%;font-family:宋体;\">具备生产胚毒活疫苗、细胞毒活疫苗及猪瘟活疫苗（兔源）的能力，</span><span style=\"line-height:150%;font-family:宋体;\">年产值达</span><span style=\"line-height:150%;\"><span>3</span></span><span style=\"line-height:150%;font-family:宋体;\">亿元。</span><span style=\"color:black;line-height:150%;font-family:宋体;\"></span></span> \r\n</p>\r\n<span> </span> \r\n<p style=\"text-indent:21pt;\">\r\n	<span><span style=\"line-height:150%;font-size:10.5pt;\">公司研发中心成立于<span>2009</span>年，</span><span style=\"color:black;line-height:150%;font-size:10.5pt;\">拥有一支强大的研发团队，</span><span style=\"line-height:150%;font-size:10.5pt;\">现有</span><span style=\"color:black;line-height:150%;font-size:10.5pt;\">研究人员</span><span style=\"line-height:150%;font-size:10.5pt;\">23</span><span style=\"line-height:150%;font-size:10.5pt;\">名，其中博士<span>2</span>人，硕士<span>7</span>人，教授<span>1</span>名，研究员<span>2</span>名，高级畜牧师<span>1</span>名，副研究员<span>1</span>名，集中了来自预防兽医学、动物病毒学、动物寄生虫学、微生物学、人畜共患病学等多方面的知名专家和优秀人才。近<span>5</span>年来先后主持和承担了“国家发改委绿色农用生物产品高技术产业化项目”“四川省科技支撑计划项目”等共计<span>20</span>余项科研项目，实验室成立至今获得国家级、省级科技奖励<span>13</span>项，获得相关专利<span>16</span>项，在国际知名期刊发表科研论文<span>32</span>篇。中心</span><span style=\"color:black;line-height:150%;font-size:10.5pt;\">建筑</span></span><span style=\"color: black; line-height: 150%; font-family: ;\" Roman?,?serif?;font-size:10.5pt;?=\"\" New=\"\" Times=\"\">1800</span><span style=\"color:black;line-height:150%;font-size:10.5pt;\"><span>平米，包含开放实验室、生物安全实验室、动物实验中心，其中十万级及以上净化区域约占</span></span><span style=\"color: black; line-height: 150%; font-family: ;\" Roman?,?serif?;font-size:10.5pt;?=\"\" New=\"\" Times=\"\">870</span><span style=\"color:black;line-height:150%;font-size:10.5pt;\"><span>平米。研发中心专用设备除基本实验外，还配备了</span></span><span style=\"color: black; line-height: 150%; font-family: ;\" Roman?,?serif?;font-size:10.5pt;?=\"\" New=\"\" Times=\"\">50L</span><span><span style=\"color:black;line-height:150%;font-size:10.5pt;\">细菌发酵系统、高效气相液相色谱、焦磷酸测序仪、蛋白纯化系统、自动微生物鉴定系统、超微量生物检测仪等高端仪器设备，能够满足实验室水平、中试水平及规模化生产各层次和多学科交叉跨越式研究的要求<span style=\"color:black;font-family:宋体;font-size:10.5pt;\">，在国内处于领先水平</span>。</span><span style=\"line-height:150%;font-size:10.5pt;\">研发中心已申请并获得四川省政府“四川省动物生物制品工程技术研究中心<span>”</span>，四川省省科技厅<span>“</span>四川省畜禽生物制品重点实验室<span>”</span>，并正在申报四川省发改委是“四川省动物生物制品工程重点实验室”。强大的研发团队为公司可持续发展奠定了坚实的基础。</span><span style=\"color: black; line-height: 150%; font-family: ;\" Roman?,?serif?;font-size:10.5pt;?=\"\" New=\"\" Times=\"\"></span></span> \r\n</p>\r\n<span> </span> \r\n<p style=\"text-indent:15.75pt;\" class=\"MsoNormal\">\r\n	<span style=\"line-height:150%;font-family:宋体;\"><span>公司将本着<span>“</span>网络、服务、诚信、共赢<span>”</span>的营销理念，在不断夯实营销网络的基础上，充分发挥资金、技术等优势，积极与高校科研单位合作，以市场和客户为中心，以专业的敏感性和前瞻性牢牢把握市场脉搏，以研究高效、绿色、无毒的兽用生物制品为发展方向，加快新产品、新剂型的开发研究工作，为我国动保行业的发展做出更大的贡献。<span style=\"color:black;\"></span></span></span> \r\n</p>\r\n<span> </span>', null, null, null, null, '0.00', '0.00', null, null, '0', null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null);
INSERT INTO `biz_hospital` VALUES ('2f55e24154ec47de863df678088c1071', '四川阿艾夫物联网产业投资有限公司', '', '否', '', '', '', '', '', '', '', '', '', '', '', '', '<p class=\"MsoNormal\" style=\"text-indent:28.0pt;\">\r\n	四川阿艾夫物联网产业投资有限公司<span>2011</span>年<span>12</span>月在成都注册成立，注册资金<span>1</span>亿元，公司总部设在双流西南航空港经济开发区物联网产业园区内<span>,</span>业务范围涵盖物联网产业项目投资管理；项目投资；房地产开发经营等物联网的各个领域。<span>2011</span>年四川阿艾夫物联网产业投资有限投资<span>18</span>亿人民币，兴建射频集成电路和智能网络技术产业园，它是以物联网产业中核心的集成电路的研发设计为龙头，集中整合云计算、传感器、<span>RFID</span>和智能网络技术的研发生产，以科技、开发、生产、应用为一体的完整产业链为规划核心。在产业特色上融合物联网产业链的研发生产为主，联合国内外知名科研机构、大学、厂商，利用本身技术优势，构建创新性园区。射频集成电路和智能网络技术产业园，占地面积超过<span>200</span>亩，总建筑面积为<span>30</span>万平方米，产业园将打造成成都乃至全国领先的产学研一体化智慧型产业园和全国最先进物联网产业生产研究生产基地。\r\n</p>', null, null, null, null, '0.00', '0.00', null, null, '0', null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null);
