INSERT INTO `biz_hospital` VALUES ('41595eb43296467c8b4a9b28e23e8e3c', '成都捷翼电子科技有限公司', '新一代电子信息', '是', '', '', '2015-09-23', '6700', '沈艳', '谭小林', '02861077361', '18030836980', '四川省成都市双流县西航港经济开发区物联大道259号6号楼', '341523133', '98', '6', ' \r\n<p style=\"text-indent:24pt;\">\r\n	成都捷翼电子科技有限公司（以下简称“捷翼”）是由广州奥翼电子科技股份有限公司、<span style=\"color:black;\">融捷投资控股集团有限公司、康宁公司（<span>Corning Incorporated</span>）共同投资组建的四川省内首家能够批量生产柔性显示器的公司，产品可广泛应用于智能卡、电子标签、移动阅读、手表、手机等领域。</span>\r\n</p>\r\n<p style=\"text-indent:24pt;\" class=\"MsoNormal\">\r\n	<span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">近年来，随着电子纸<span>(E-paper)</span>及有机</span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">EL(OLED)</span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">这类显示产品相继问世，新兴显示技术的焦点逐渐转向柔性显示，柔性面板技术逐渐成为显示屏产业的下一个发展重点区域之一。捷翼的柔性显示器制造项目总投资<span>5</span>亿元，其中一期总投资<span>3</span>亿元，主要从事柔性电子纸的批量生产。公司坐落于双流县物联网产业园，预计<span>2016</span>年<span>8</span>月开始生产，预计项目投产后<span>5</span>年销售总额可达<span>20</span>亿元，项目的有关技术处于世界领先水平，可填补四川省内显示领域的技术空白。<span></span></span>\r\n</p>\r\n<p style=\"text-align:left;text-indent:24pt;\" class=\"MsoNormal\" align=\"left\">\r\n	<span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">为突破新型显示产业的核心技术，捷翼研发团队做了深入研究。研发团队通过近<span>10</span>年的不懈努力，开发出适用于电泳显示电子纸及有机发光二极管的组件，利用有机薄膜晶体管背板的经验，结合材料与设备商的支持，将有机薄膜晶体管背板技术成功实现于柔性显示器产品上，实现了卷对卷柔性电子纸显示屏的量产技术，突破了国际新型显示产业的核心技术，并取得相关专利。捷翼将是全世界第一个采用卷对卷方式生产柔性显示器的公司，成功投产后将为新型显示产业带来新一轮技术革新。<span></span></span>\r\n</p>\r\n<p style=\"text-align:left;text-indent:24pt;\" class=\"MsoNormal\" align=\"left\">\r\n	<span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">本项目建成后将实现年产柔性电子纸<span>4000</span>万张，仅智能卡柔性显示方面，就有上百亿的市场。</span><span style=\"line-height:150%;font-size:12pt;\">2015</span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">年初，央行发布《</span><span style=\"line-height:150%;font-size:12pt;\">2014</span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">年支付体系运行总体情况》，截至</span><span style=\"line-height:150%;font-size:12pt;\">2014</span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">年第四季度末，全国累计发行银行卡</span><span style=\"line-height:150%;font-size:12pt;\">49.36</span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">亿张，按每张银行卡售价</span><span style=\"line-height:150%;font-size:12pt;\">10</span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">元，市场容量为</span><span style=\"line-height:150%;font-size:12pt;\">493.6</span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">亿元；与金融</span><span style=\"line-height:150%;font-size:12pt;\">IC</span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">卡相对应的是社保卡，据人社部统计：截至</span><span style=\"line-height:150%;font-size:12pt;\">2014</span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">年底，全国社保卡持卡人数达到</span><span style=\"line-height:150%;font-size:12pt;\">7.12</span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">亿人，覆盖全国一半以上人口，全年新增发卡</span><span style=\"line-height:150%;font-size:12pt;\">1.64</span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">亿。按每张社保卡售价</span><span style=\"line-height:150%;font-size:12pt;\">8</span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">元，市场容量为</span><span style=\"line-height:150%;font-size:12pt;\">56.96</span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">亿元；在城市公共交通卡应用方面，截止</span><span style=\"line-height:150%;font-size:12pt;\">2014</span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">年底，全国</span><span style=\"line-height:150%;font-size:12pt;\">600</span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">个城市中近</span><span style=\"line-height:150%;font-size:12pt;\">500</span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">个城市开通了城市公共交通卡电子收费系统，发卡累计达</span><span style=\"line-height:150%;font-size:12pt;\">5</span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">亿多张，按售价</span><span style=\"line-height:150%;font-size:12pt;\">10</span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">元，市场容量为</span><span style=\"line-height:150%;font-size:12pt;\">50</span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">亿元。</span><span style=\"line-height:150%;font-family:宋体;font-size:12pt;\">本项目投产后，不仅捷翼本身能够获得良好的收益，对于推进产业结构转型升级，带动相关产业链的发展，提升成都的知名度也有积极的帮助。<span></span></span>\r\n</p>', null, null, null, null, '0.00', '0.00', null, null, '0', null, '122eeq1', null, 'wd11w', null, 'qw21ws', 'IT类,建筑类', '1', '2016-11-11', '1', '5', '2016-12-12', '2', '4', '2016-12-13', '3', '3', '2016-12-14', '4', '2', '2016-12-15', '5', '1', '2', '2');
