INSERT INTO `biz_hospital` VALUES ('4b5d8c6f425f478399f177692934a718', '成都欧赛医疗器械有限公司', '医疗器械', '是', '2013-11-18', '2016-11-18', '2005-01-09', '2000', '甘释良', '彭迪', '67085899', '13678123071', '西航港经济开发区内', '91510122768647821B', '170', '', '成都欧赛医疗器械有限公司成立于2005年，是一家专业致力于研发、生产和销售血液净化系列及 其相关医疗器械产品的具有独立法人资格的高新技术企业。是国内第一家取得高通量透析器产品注册证 的企业，拥有完全的高通量血液透析器自主知识产权。<br />\r\n        成都欧赛医疗器械有限公司是国内第一家取得高通量 血液透析器产品注册证的企业，拥有改性聚醚砜中空纤维膜应用于血液透析器临床使用的发明专利、拥 有自主知识产权的聚醚砜中空纤维膜纺制技术、拥有自主知识产权的血液透析器封装技术及生产线。<br />\r\n        公司自成立伊始，便与四川大学高分子材料学院建立 了联合实验室，共同致力于生物膜材料在血液净化应用的研发及临床治疗。通过长期不断的创新和努力 ，欧赛医疗掌握了血液净化系列产品研发与制造的核心技术、获得了众多与之相关的发明专利，并将长 期致力于中国血液净化产业规模化发展，为国产医疗器械的崛起而不懈努力。<br />\r\n        目前成都欧赛医疗器械有限公司位于成都市西航港开 发区的生产基地，占地50亩，建筑面积4.7万平方米，拥有员工200余人，公司集合各类专业人才，产品 研制开发范围涵盖：血液透析器、血液透析机、血液滤过器、血液灌流器、穿刺针、体外循环管路、透 析液和透析粉、透析护理包、透析机消毒液等血液净化全系列类品。<br />\r\n<br />\r\n<br />', null, null, null, null, '0.00', '0.00', null, null, '0', null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null);
INSERT INTO `biz_hospital` VALUES ('4b80229d22a94c3b806eb24ac0517377', '四川神工钨钢刀具有限公司', '制造业/新材料', '否', '', '', '2012-12-25', '5600', '黄鸿春', '刘传', '02887988738', '15882070108', '成都市双流区西南航空港经济开发区空港三路3119号', '9151012205748881XA', '160', '8项', '<span> </span> \r\n<p style=\"text-align:center;\" align=\"center\">\r\n	<b><span style=\"line-height:150%;font-family:宋体;font-size:14pt;\">四川神工钨钢刀具有限公司</span></b><span style=\"font-family:宋体;font-size:14pt;\"><strong>简介</strong></span>\r\n</p>\r\n<span> </span>\r\n<p style=\"color:#000000;font-style:normal;font-weight:normal;\">\r\n	&nbsp;\r\n</p>\r\n<p style=\"color:#000000;font-style:normal;font-weight:bold;\">\r\n	<ol>\r\n		<li style=\"color:#000000;font-family:宋体;font-size:14pt;font-style:normal;font-weight:normal;\">\r\n			<p style=\"color: rgb(0, 0, 0); font-family: ;\" Roman?,?serif?;font-size:10.5pt;font-style:normal;font-weight:normal;?=\"\" New=\"\" Times=\"\"> <span style=\"font-family:宋体;font-size:14pt;\">四川神工钨钢刀具有限公司是专业从事特种钨钢刀具研发生产的科技型生产企业；公司位于</span><span style=\"font-family:宋体;font-size:14pt;\">成都市双流区西南航空港经济开发区空港<span>3</span>路<span>3119</span>号；</span><span style=\"background:white;font-family:宋体;font-size:14pt;\">公司有拥有全套钨钢坯料生产线和钨钢坯料精磨加工所需全部三百多台（套）生产检测设备，为国内生产同类产品的翘首企业。</span> \r\n				</p>\r\n					</li>\r\n					<li style=\"color:#000000;font-family:宋体;font-size:14pt;font-style:normal;font-weight:normal;\">\r\n						<p style=\"color: rgb(0, 0, 0); font-family: ;\" Roman?,?serif?;font-size:10.5pt;font-style:normal;font-weight:normal;?=\"\" New=\"\" Times=\"\"> <span style=\"background:white;font-family:宋体;font-size:14pt;\">公司拥有从湿磨开始的硬质合金生产线和硬质合金机加工生产线。</span><span style=\"font-family:宋体;font-size:14pt;\">公司现有员工<span>160</span>余人，其中本科<span>12</span>人、大专<span>22</span>人；<span style=\"background:white;\">近年来随着公司先进设备的不断引进和技改项目的陆续完工，公司产品在内在品质及加工精度上，都得到了较大的提升。公司产品一半以上出口到欧美等发达国家和地区，其中公司研发生产的钨钢圆切刀系列产品获四川省和国家科技创新奖励。</span></span> \r\n							</p>\r\n								</li>\r\n								<li style=\"color:#000000;font-family:宋体;font-size:14pt;font-style:normal;font-weight:normal;\">\r\n									<p style=\"color: rgb(0, 0, 0); font-family: ;\" Roman?,?serif?;font-size:10.5pt;font-style:normal;font-weight:normal;?=\"\" New=\"\" Times=\"\"> <span style=\"font-family:宋体;font-size:14pt;\">为了让公司产品从规模、结构和质量上更上一个台阶，满足不断增加的国内外客户的需求，</span><span style=\"font-family:宋体;font-size:14pt;\">四川神工钨钢刀具有限公司<span>决定在天府新区双流西南航空港经济开发区内征地<span>41.53</span>亩，拟投资<span>1.2</span>亿元人民币新建“锂电极片钨钢刀具生产基地</span>”<span>项目，该项目建成后将形成年产<span>50</span>万片</span>锂电极钨钢刀具刀片<span>的生产能力，总建筑面积约<span>24620m<sup>2</sup></span>，主要建设内容包括修建生产厂房、检验中心、包装成品库及员工宿舍等，公司将于<span>2016</span>年年底整体搬迁至新址。</span></span> \r\n										</p>\r\n											</li>\r\n											<li style=\"color:#000000;font-family:宋体;font-size:14pt;font-style:normal;font-weight:normal;\">\r\n												<p style=\"color: rgb(0, 0, 0); font-family: ;\" Roman?,?serif?;font-size:10.5pt;font-style:normal;font-weight:normal;?=\"\" New=\"\" Times=\"\"> <span style=\"font-family:宋体;font-size:14pt;\">公司坚持：以人为先、诚信为本的人才理念，质量第一，注重细节的质量理念；顾客至上，互惠共赢的服务理念：节能降耗，保护环境的环境理念和讲求效率，持续创新的行为准则。</span> \r\n													</p>\r\n														</li>\r\n														<li style=\"color:#000000;font-family:宋体;font-size:14pt;font-style:normal;font-weight:normal;\">\r\n															<p style=\"color: rgb(0, 0, 0); font-family: ;\" Roman?,?serif?;font-size:10.5pt;font-style:normal;font-weight:normal;?=\"\" New=\"\" Times=\"\"> <span style=\"font-family:宋体;font-size:14pt;\">公司秉承人性化管理模式，注重员工的工作和生活环境的投入，着重员工的福利和权益保障，使员工工作顺心，生活舒心。</span> \r\n																</p>\r\n																	</li>\r\n																		</ol>\r\n																			</p>\r\n<span> </span>', null, null, null, null, '0.00', '0.00', null, null, '0', null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null);
