INSERT INTO `biz_hospital` VALUES ('501afbd3ee4249559279675663d4591b', '成都一本厨房工程有限公司', '', '否', '', '', '', '', '', '', '', '', '', '', '', '', '<p class=\"MsoNormal\" style=\"margin-left:0cm;text-indent:30.0pt;\">\r\n	成都一本厨房工程有限公司成立于<span>2013</span>年，专业为高端酒店、宾馆、学校、酒楼等厨房提供设计、咨询、承建及安装不锈钢厨房设备和通风系统配套工程。公司拥有标准化生产厂房，公司生产的“一本”牌不锈钢厨具用料讲究，设计先进、实用性强、性价比高，在客户中拥有良好的口碑。<span></span>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"margin-left:0cm;text-indent:30.0pt;\">\r\n	公司拥有资深的设计、生产人员，对餐饮文化和厨房设计有相当深入的理解。同时与沿海最优秀的厨房顾问公司和厨房生产公司保持深入的技术交流与学习，适时保持最先进的设计理念与生产工艺。同时拥有先进的生产设备，优质的工艺及成熟的生产和工程队伍。在多年的生产实践中积累了丰富的工程经验，并树立了良好的企业形象。<span></span>\r\n</p>', null, null, null, null, '0.00', '0.00', null, null, '0', null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null);
