INSERT INTO `biz_hospital` VALUES ('612a59b98b044a7c9ccbc884190fd5e2', '成都禅德太阳能电力有限公司', '新能源', '是', '2014-10-11', '2017-10-11', '2011-02-24', '20000.00', '薛黎明', '王秀明', '13683113235', '13408529720', '成都市双流区西南航空港经济开发区工业集中区内', '91510122569663028M', '76', '5项', '<span> </span>\r\n<p style=\"text-indent:28pt;\">\r\n	<span style=\"font-family:宋体;font-size:14pt;\">成都禅德太阳能电力有限公司作为中海阳能源集团控股子公司，定位为专业的太阳能聚光热发电镜场设备供应商，注册资本为<span>20</span>，<span>000</span>万元人民币，是四川省积极承接产业转移，建设辐射西部、面向全国、融入世界的西部经济发展高地的重点引进项目之一。<span> <br />\r\n</span> 公司以发展为现代化大型光热发电设备制造企业为目标，以研发、生产和销售太阳能聚光热发电设备及产品为主营业务，组建专业技术团队建设太阳能光热发电聚光镜，实现年<span>420</span>万平方米槽式反射镜的生产能力。<span> </span></span>\r\n</p>\r\n<span> </span>\r\n<p style=\"text-indent:28pt;\">\r\n	<span style=\"font-family:宋体;font-size:14pt;\">未来，成都禅德将秉承中海阳集团“实现自我、惠泽社会、造福子孙”的历史使命，为推动中国太阳能光热发电产业发展贡献力量<span>! </span></span>\r\n</p>\r\n<span> </span>', null, null, null, null, '0.00', '0.00', null, null, '0', null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null);
