INSERT INTO `biz_hospital` VALUES ('637480c91f2948ec85386946e80daf59', '四川国氏生物科技有限公司', '生物科技', '否', '', '', '2015-09-01', '1000', '刘开春', '张女士', '028-64009617', '028-64009617', '西航港空港二路238号', '91510122MA61R0L0X2', '30', '', '<p class=\"MsoNormal\">\r\n	四川国氏生物科技有限公司成立于2015年6月，是由国内外知名生物学家团队，联合天使投资人团队，共同组建的新型生物医药技术公司。\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	      公司专注于创新生物安全药的开发，以全球重大的原创性技术，及一类全球首次发现的小分子先导物等，帮助解决当今世界面临的一些最严峻的挑战。其中包括：一、病毒的感染与泛滥，二、抗生素滥用与细菌耐药，三、农药残留以及农作物安全等方面。业务涵盖全球免疫药、抗病毒药、细菌药，以及保健食品等领域。\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	      公司<span>目前正在实施全球重大生物技术成果产业化业务，将有望引领全球医学进入</span>“后抗生素”的新时代。机遇与挑战并存的同时，公司秉承着创新、务实、开放、分享的经营理念<span>诚邀社会精英人力加盟，</span><span>希望通过</span><span>我们</span><span>现实的努力，以及对</span><span>卓越的</span><span>的</span><span>不懈追求，成就公司特有的企业文化</span><span>。</span><span>同时公司将</span><span>为您提供富有竞争力的薪酬待遇，为</span><span>有志</span><span>之士提供良好的发展空间和平台。</span>\r\n</p>', null, null, null, null, '0.00', '0.00', null, null, '0', null, null, null, null, null, null, null, '2', null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null);
