INSERT INTO `biz_hospital` VALUES ('6c94c588d99b491988481fbbec9ee1ee', '成都托克密封件有限责任公司', '制造业', '否', '', '', '2003-05-23', '550', '苏东', '刘秀林', '13730853089', '13880326069', '成都市双流区西南航空港经济开发区腾飞四路', '91510122749723139M', '100', '8', '<p>\r\n	<img style=\"width:140px;height:34px;\" src=\"http://www.cdtk.com.cn/ueditor136/jsp/upload/1466730925452.png\" /><span style=\"font-size:22px;font-family:华文中宋;color:#993300;background-color:#FDEADA;\">托蛟出海需磨砺&nbsp; 克险腾云终成龙</span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"font-size:22px;font-family:华文中宋;color:#993300;background-color:#FDEADA;\"></span><span style=\"font-size:22px;font-family:华文中宋;color:#993300;background-color:#FDEADA;\"></span><span style=\"font-family:华文中宋;color:#BA0003;font-size:36px;\">成都托克密封件有限责任公司</span><span style=\"font-size:36px;\"><span style=\"font-size:22px;font-family:;\"><br />\r\n</span></span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"font-size:36px;\"><span style=\"font-size:22px;font-family:;\">ChengDu</span> <span style=\"font-size:22px;font-family:;\">TuoKe Sealing Element Co ., Ltd</span></span> \r\n</p>\r\n<span style=\"font-size:24px;background-color:#E36C09;\"><strong><span style=\"font-size:24px;background-color:#E36C09;font-family:楷体_GB2312;color:#0000CC;\">企业简介</span></strong></span> \r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<span style=\"font-size:22px;font-family:华文中宋;color:#993300;background-color:#FDEADA;\"><span style=\"font-size:24px;background-color:#E36C09;\"><strong><span style=\"font-size:24px;background-color:#E36C09;font-family:楷体_GB2312;color:#0000CC;\"></span></strong></span><span style=\"font-size:20px;\"><strong><span style=\"font-size:20px;color:blue;\"><strong><span style=\"font-size:22px;font-family:宋体;color:blue;\">成都托克密封件有限责任公司创建于2003年5月，是一家专业生产橡胶密封件的企业。公司位于成都西南航空港经济开发区，紧邻双流国际机场，背靠成雅高速，\r\n临近空港物流园区，交通物流十分便利。公司占地面积13200平方米，建筑面积10000多平米。&nbsp;全套引进国际一流的真空硫化机和成套检测设备。 \r\n2009年公司通过了ISO/TS16949质量体系认证。是同行业中率先通过IATF（全球汽车协会）JAMA（日本汽车协会）认证的企业，并获得国家\r\n技术监督总局颁发的全国工业生产许可证。</span></strong></span></strong></span></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:22px;font-family:华文中宋;color:#993300;background-color:#FDEADA;\"><span style=\"font-size:20px;\"><strong><span style=\"font-size:20px;color:blue;\"><strong><span style=\"font-size:22px;font-family:宋体;color:blue;\"><span style=\"font-size:24px;background-color:#E36C09;\"><strong><span style=\"background-color:#E36C09;font-size:24px;font-family:楷体_GB2312;color:#0000CC;\">企业荣誉</span></strong></span></span></strong></span></strong></span></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:22px;font-family:华文中宋;color:#993300;background-color:#FDEADA;\"><span style=\"font-size:20px;\"><strong><span style=\"font-size:20px;color:blue;\"><strong><span style=\"font-size:22px;font-family:宋体;color:blue;\"><span style=\"font-size:24px;background-color:#E36C09;\"><strong><span style=\"background-color:#E36C09;font-size:24px;font-family:楷体_GB2312;color:#0000CC;\"></span></strong></span> </span></strong></span></strong></span></span> \r\n</p>\r\n<p style=\"vertical-align:baseline;\">\r\n	<strong><strong><span style=\"font-size:20px;\"><strong><span style=\"font-size:20px;color:blue;\">2009年-2015年</span></strong></span> </strong></strong> \r\n</p>\r\n<p style=\"vertical-align:baseline;\">\r\n	<strong><strong><span style=\"font-size:20px;\"><strong><span style=\"font-size:20px;color:blue;\"><strong>1、 <span style=\"font-size:22px;font-family:宋体;color:blue;\">荣获四川省中小企业</span></strong></span></strong></span><span style=\"font-size:20px;\"><strong><span style=\"font-size:20px;color:blue;\"><strong><span style=\"font-size:22px;font-family:宋体;color:blue;\">局、四川省工商联合会授予的“</span><span style=\"font-size:22px;font-family:\'Calibri\',\'sans-serif\';color:blue;\">2009</span><span style=\"font-size:22px;font-family:宋体;color:blue;\">年四川省成长型中小企业”称</span></strong></span></strong></span><span style=\"font-size:20px;\"><strong><span style=\"font-size:20px;color:blue;\"><strong><span style=\"font-size:22px;font-family:宋体;color:blue;\">号</span></strong></span></strong></span> </strong></strong> \r\n</p>\r\n<p style=\"vertical-align:baseline;\">\r\n	<strong><span style=\"font-size:20px;\"><strong><span style=\"font-size:20px;color:blue;\">2<strong><span style=\"font-size:22px;font-family:宋体;color:blue;\">、荣获四川省维权防伪中心授予的“四川省产品质量稳定合格企业”称号</span></strong></span></strong></span> </strong> \r\n</p>\r\n<p style=\"vertical-align:baseline;\">\r\n	<strong><span style=\"font-size:20px;\"><strong><span style=\"font-size:20px;color:blue;\">3、 <strong><span style=\"font-size:22px;font-family:宋体;color:blue;\"></span></strong></span><span style=\"font-size:20px;color:blue;\"></span></strong></span><span style=\"font-size:20px;\"><strong><span style=\"font-size:20px;color:blue;\"><strong><span style=\"font-size:22px;font-family:宋体;color:blue;\">荣获成都市经济和信息化委员会与成都市中小企业局评选的“成都之星--最具发展中小企业100强”排名78位殊荣</span></strong></span></strong></span> </strong> \r\n</p>\r\n<p style=\"vertical-align:baseline;\">\r\n	<strong><span style=\"font-size:20px;\"><strong><span style=\"font-size:20px;color:blue;\"><strong><span style=\"font-size:22px;font-family:宋体;color:blue;\"><span style=\"font-size:20px;\"><strong><span style=\"font-size:20px;color:blue;\">4、</span></strong></span><span style=\"font-size:20px;\"><strong><span style=\"font-size:20px;color:blue;\"><strong><span style=\"font-size:22px;font-family:宋体;color:blue;\">荣获多项实用新型专利证书</span></strong></span></strong></span></span></strong></span></strong></span> </strong> \r\n</p>\r\n<p style=\"vertical-align:baseline;\">\r\n	<span style=\"font-size:20px;\"><strong><span style=\"font-size:20px;color:blue;\"><strong><span style=\"font-size:22px;font-family:宋体;color:blue;\"><span style=\"font-size:20px;\"><strong><span style=\"font-size:20px;color:blue;\"><strong><span style=\"font-size:22px;font-family:宋体;color:blue;\"></span></strong></span></strong></span></span></strong></span></strong></span> \r\n</p>\r\n<p style=\"vertical-align:baseline;\">\r\n	<span style=\"font-size:20px;\"><strong><span style=\"font-size:20px;color:blue;\"><strong>5、<span style=\"font-size:22px;font-family:宋体;color:blue;\">荣获“中航工业成都成发汽车发动机有限公司</span><span style=\"font-size:22px;font-family:\'Calibri\',\'sans-serif\';color:blue;\">2010</span><span style=\"font-size:22px;font-family:宋体;color:blue;\">年度优秀供应商”</span></strong></span></strong></span> \r\n</p>\r\n<p style=\"vertical-align:baseline;\">\r\n	<span style=\"font-size:20px;\"><strong><span style=\"font-size:20px;color:blue;\"><strong><span style=\"font-size:22px;font-family:宋体;color:blue;\">6、荣获“沈阳新光华翔汽车发动机制造有限公司2014优秀供应商”</span></strong></span></strong></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:20px;\"><strong><span style=\"font-size:20px;color:blue;\"><strong><span style=\"font-size:22px;font-family:宋体;color:blue;\">7、荣获“杭州易辰孚特汽车零部件有限公司2015年优秀供应商”</span></strong></span></strong></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:20px;\"><strong><span style=\"font-size:20px;color:blue;\"><strong><span style=\"font-size:22px;font-family:宋体;color:blue;\"><br />\r\n</span></strong></span></strong></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:20px;\"><strong><span style=\"font-size:20px;color:blue;\"><strong><span style=\"font-size:22px;font-family:宋体;color:blue;\"><img src=\"/zqt/uploads/2016/09/01/923823df5e3e4e53a09edc035632301c.jpg\" alt=\"\" height=\"260\" width=\"586\" /><br />\r\n</span></strong></span></strong></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:20px;\"><strong><span style=\"font-size:20px;color:blue;\"><strong><span style=\"font-size:22px;font-family:宋体;color:blue;\"><img src=\"/zqt/uploads/2016/09/01/b704ca3eb28e44f0af260362655e02af.jpg\" alt=\"\" /></span></strong></span></strong></span> \r\n</p>\r\n<p>\r\n	<img src=\"/zqt/uploads/2016/09/01/928c374352ad4107b54bd8eceab63ea4.jpg\" alt=\"\" height=\"215\" width=\"578\" /> \r\n</p>\r\n<p>\r\n	<img src=\"/zqt/uploads/2016/09/01/cc022408bf894e4381e0184591477394.jpg\" alt=\"\" he