INSERT INTO `biz_hospital` VALUES ('71bf0b0276d041b2b0523475fc7c96e0', '成都当肯车业有限公司', '', '否', '', '', '2009-01-06', '500', '杨世森', '梁海蓉', '85716953', '15184466978', '双流区西南航空港经济开发区', '915101226818480140', '136', '', '<p class=\"MsoNormal\">\r\n	<span style=\"font-family:宋体;font-size:14.0000pt;\"> </span>\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-family:宋体;font-size:14.0000pt;\"> 成都当肯车业有限公司始创于</span><span style=\"font-family:宋体;font-size:14.0000pt;\">2009<span>年</span><span>1</span><span>月，位于成都市双流县西航港空港经济开发区，</span><span>2012</span><span>年</span><span>3</span><span>月开始试生产，集电动自行车、电动摩托车整车研发、制造、配套供应、销售、售后为一体的现代化企业，年生产能力</span><span>7.5</span><span>万套。</span></span><span style=\"font-family:宋体;font-size:14.0000pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-family:宋体;font-size:14.0000pt;\"> 企业固定资产总值<span>1.2</span><span>亿元，占地</span><span>32</span><span>亩，拥有员工总数</span><span>130</span><span>余人，其中具有中高职称的技术人员</span><span>11</span><span>余人。公司拥有一支朝气蓬勃、敬业精干、激情、智慧丰富、创新高效的技术研发管理团队，拥有先进的开发制造工艺水平、整车配件检测设备以及严格而规范的质量管理体系，时常关注“产品在你手中，质量在我心中”，企业以高度的责任感和使命感投入到生产中去，专注每一个细节，严格把关，一丝不苟，把优质的产品送到每一个用户手中！</span></span>\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-family:宋体;font-size:14.0000pt;\"><span>&nbsp;&nbsp; 2015年，我公司正式进入新能源汽车领域，取得“知豆”牌<span style=\"font-family:宋体;font-size:14.0000pt;\"><span>新能源电动汽车四川省代理权，公司提供知豆汽车整车销售、服务、按揭、保险、上户、售后维修等全方位的客户服务。</span></span><br />\r\n</span></span>\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	&nbsp;&nbsp;&nbsp; <br />\r\n<span style=\"font-family:宋体;font-size:14.0000pt;\"><span></span></span><span style=\"font-family:宋体;font-size:14.0000pt;\"></span> \r\n</p>\r\n<br />\r\n<p>\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-family:宋体;font-size:14.0000pt;\"><span></span><span></span><span></span><span></span><span></span><span></span><span></span><span></span></span><span style=\"font-family:宋体;font-size:14.0000pt;\"></span> \r\n</p>', null, null, null, null, '0.00', '0.00', null, null, '0', null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null);
INSERT INTO `biz_hospital` VALUES ('71c4b21a299249509ed4dd87cb2174c5', '成都泉源卫生用品有限公司', '制造业', '否', '', '', '1994-12-14', '561.73', '刘荣富', '许杰妤', '028-85880321', '028-85880322', '四川省成都市双流区航空港经济开发区空港二路415号', '91510122621708255L', '98', '8个', '<p class=\"MsoNormal\">\r\n	成都泉源卫生用品有限公司成立于<span>1994</span>年，是成都彩虹电器（集团）股份有限公司与日本住商山美德化学株式会社、日本阿斯制药株式会社合资兴建的公司，由中方控股。公司地址位于成都市高新区桂溪工业园，多年来着重于家庭卫生用品方面的研制、生产、销售。本公司生产销售的彩虹系列产品一直以质优价廉，售后服务完善而深受广大销费者的喜爱。<span></span>\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	&nbsp;&nbsp;&nbsp; 公司现有职工<span>85</span>余人，工程技术人员<span>15</span>人。泉源公司经多年的研制开发，以在电热蚊香液、杀虫气雾剂等夏季家庭卫生用品方面形成了一定的产销规模。夏季产品相继开发了清香型液体蚊香、无味型液体蚊香、超长效用液体蚊香，年生产<span>400</span>万余瓶、液体蚊香器<span>200</span>万只。全害虫杀虫剂、专业杀蟑气雾剂年生产<span>180</span>万罐。另外还生产冬季畅销产品烘鞋器<span>30</span>万双。公司曾分别获得成都市武侯区“重点企业”、“纳税大户”、等多项殊荣。<span></span>\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	&nbsp;&nbsp;&nbsp; 彩虹公司属国家二级企业，全公司推行全面质量管理，在各政府职能监督部门的监督下进行生产。液体蚊香车间占地约<span>1600m2</span>，配备有搅拌缸，过滤器，自动灌装机，热缩机、装配工装、打包机等。杀虫剂车间占地约<span>2000m2</span>，配备有除臭，搅拌缸，贮液缸，自动灌装线，热缩机、打包机等。我公司严格质量监督管理，质检部门对每一进厂原料进行全面质量分析，对进厂主要材料和产成品测试分，建立了化学分析室、养蚊室、生物药效室、配置了对产成品进行严格的出厂检验和型式检验，按照<span>ISO9001-2000</span>要求，建立了质量保证体系。在竞争激烈的市场上，我们生产的家庭卫生杀虫剂各类产品以干净、卫生、微毒、使用方便、杀灭效果迅速等优势立足于市场，受到广大用户的喜爱和好评长确认。泉源公司全体员工视质量为企业的生命，全体上下严格把好每一道关，确保不合格品不出厂，出厂的都是合格产品。<span></span>\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	&nbsp;&nbsp;&nbsp; 成都泉源卫生用品有限公司在董事长兼法人代表刘荣富、公司董事和总经理吴远兵的领导下，公司正在加强提高老产品的基础上不断开发新产品，为丰富广大消费者的现代生活，为提高广大人民的健康水平而努力，正欣欣向荣地迎接改革开放的浪潮，欢迎各主管部门领导到公司光临指导。<span></span>\r\n</p>', null, null, null, null, '0.00', '0.00', null, null, '0', null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null);
INSERT INTO `biz_hospital` VALUES ('720cd80ce9a64c528a4c2555ea84a0c8', '四川天味食品集团股份有限公司', '农产品精深加工', '否', '', '', '2007-03-02', '36675', '邓文', '吴兰', '028-82808188', '028-82808111', '成都市双流区西航港街道腾飞一路333号', '915101007978308873', '1573', '11', '<div class=\"sub-4\" style=\"border:none;color:#333333;font-family:宋体, Arial;background-color:#EFF7EE;\">\r\n	<div align=\"center\" class=\"sub-4-2\" style=\"border:none;padding:0px;text-align:center;\">\r\n	</div>\r\n	<div>\r\n	</div>\r\n</div>\r\n<div class=\"sub-5\" style=\"border:none;color:#333333;font-family:宋体, Arial;background-color:#EFF7EE;\">\r\n	<div align=\"left\" class=\"sub-5-1\" style=\"border:none;padding:0px;\">\r\n		<p class=\"MsoNormal\" style=\"text-align:justify;font-size:10.5pt;font-family:Calibri, sans-serif;text-indent:28pt;\">\r\n			<span style=\"font-family:宋体;font-size:14pt;\">四川天味食品集团股份有限公司（以下简称“天味食品”或“公司”）位于双流县西南航空港街道腾飞一路</span><span style=\"font-size:14pt;\">333</span><span style=\"font-family:宋体;font-size:14pt;\">号，是中国调味品协会理事单位、农业产业化国家重点龙头企业。公司现有四川天味家园食品有限公司、自贡市天味食品有限公司以及四川瑞生投资管理有限公司三家全资子公司。</span> \r\n		</p>\r\n		<p class=\"MsoNormal\" style=\"text-align:justify;font-size:10.5pt;font-family:Calibri, sans-serif;text-indent:28pt;\">\r\n			<span style=\"font-family:宋体;font-size:14pt;\">自成立以来，天味食品一直致力于川味复合调味料的研发、生产和销售，现已自主研发出“大红袍”、“好人家”、“天车”、“羊羊羊”四大系列畅销全国并远销海外的产品。其中，“大红袍”、“好人家”牌川味复合调味料获“四川名牌产品”称号；“天车”商标被中华人民共和国商务部认定为“中华老字号”。</span> \r\n		</p>\r\n		<p class=\"MsoNormal\" style=\"text-align:justify;font-size:10.5pt;font-family:Calibri, sans-serif;text-indent:28pt;\">\r\n			<span style=\"font-family:宋体;font-size:14pt;\">作为全国领先的川味复合调味料生产企业，<span style=\"font-size:14pt;\">公司历来注重规范管理，并先后通过了</span></span><span style=\"font-size:14pt;\">ISO9001</span><span style=\"font-family:宋体;font-size:14pt;\">质量管理体系认证、</span><span style=\"font-size:14pt;\">ISO22000</span><span style=\"font-family:宋体;font-size:14pt;\">食品安全管理体系认证、</span><span style=\"font-size:14pt;\">ISO14001</span><span style=\"font-family:宋体;font-size:14pt;\">环境管理体系认证以及</span><span style=\"font-size:14pt;\">OHSAS18001</span><span style=\"font-family:宋体;font-size:14pt;\">职业健康安全管理体系认证。</span> \r\n		</p>\r\n		<p class=\"MsoNormal\" style=\"text-align:justify;font-size:10.5pt;font-family:Calibri, sans-serif;text-indent:28pt;\">\r\n			<span style=\"font-family:宋体;font-size:14pt;\">规范的管理，为天味食品持续创新提供了坚实保障。公司与行业机构及专业院校建立了深度的战略合作关系，形成了产、学、研一体化的专业队伍，凝聚了一大批专业的高素质的研发人才。此外，公司是火锅底料、辣椒酱国家标准的起草单位之一，也参与了川式复合调味料、川式佐餐调味料等地方标准的起草。规范的管理、持续的创新也促进了销售的稳定增长。目前公司已在全国</span><span style=\"font-size:14pt;\">31</span><span style=\"font-family:宋体;font-size:14pt;\">个省市自治区建立了完善的销售网络，并在重要区域设置了办事处。随着销售网络全球化战略的实施，产品已远销美国、英国、加拿大、澳大利亚等国家。</span> \r\n		</p>\r\n		<p class=\"MsoNormal\" style=\"text-align:justify;font-size:10.5pt;font-family:Calibri, sans-serif;text-indent:28pt;\">\r\n			<span style=\"font-family:宋体;font-size:14pt;\">在未来，天味食品将继续秉承“实、容、新、先”的企业精神，以“用心调味生活”为经营理念，紧紧把握行业高速发展的机遇，稳健发展，不断超越，用优质的产品服务于广大消费者！</span> \r\n		</p>\r\n	</div>\r\n</div>', null, null, null, null, '0.00', '0.00', null, null, '0', null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null);
INSERT INTO `biz_hospital` VALUES ('726f726e8c6d48689ed46b998d0f6e22', '四川飞阳科技有限公司', '电子信息', '是', '2015-10-09', '2018-10-08', '2004-12-29', '6577.8', '吴雄飞', '李朝阳', '', '028-67037788', '四川省成都市双流县西南航空港街道长城路一段185号', '510100400015808', '240', '已授权发明专利16项，实用新型专利14项；成功申请发明专利29项，国际专利3项', '<p class=\"MsoNormal\" style=\"text-indent:28.3pt;\">\r\n	<span style=\"font-size:14.0pt;font-family:&quot;\"><span style=\"font-size:14px;\">四川飞阳科技有限公司是国家高新技术企业，拥有世界领先的光电集成芯片制造技术，由“国家特聘专家”李朝阳博士和四川省著名企业四川雄飞集团于</span><span style=\"font-size:14px;\">2004</span><span style=\"font-size:14px;\">年</span><span style=\"font-size:14px;\">12</span><span style=\"font-size:14px;\">月发起创立。总投资</span><span style=\"font-size:14px;\">5</span><span style=\"font-size:14px;\">亿元的“光电集成芯片”项目落户于成都市双流县，项目已于</span><span style=\"font-size:14px;\">08</span><span style=\"font-size:14px;\">年</span><span style=\"font-size:14px;\">10</span><span style=\"font-size:14px;\"><span style=\"font-size:14px;\">月投产。</span>\r\n	<p class=\"MsoNormal\" style=\"text-indent:28.3pt;\">\r\n		<span style=\"font-size:14.0pt;font-family:&quot;\"><span style=\"font-size:14px;\">公司在国内率先开展平面光波导技术（</span><span style=\"font-size:14px;\">PLC</span><span style=\"font-size:14px;\">）研究，并成功实现了产业化，是国内唯一掌握</span><span style=\"font-size:14px;\">PLC</span><span style=\"font-size:14px;\">芯片核心制造技术的企业。</span><span></span></span><span style=\"font-size:14px;font-family:&quot;\">PLC</span><span style=\"font-size:14.0pt;font-family:&quot;\"><span style=\"font-size:14px;\">技术是光通讯器件制造技术的发展方向，该技术能将目前分立的光电器件集成在极小的硅片上，而且生产成本低、效率高，目前仅韩国、日本的少数厂商掌握核心技术并商用化。</span><span></span></span>\r\n	</p>\r\n	<p class=\"MsoNormal\" style=\"text-indent:28.3pt;\">\r\n		<span style=\"font-size:14.0pt;font-family:&quot;\"><span style=\"font-size:14px;\">公司在</span><span style=\"font-size:14px;\">PLC</span><span style=\"font-size:14px;\">技术上已申请</span><span style=\"font-size:14px;\">20</span><span style=\"font-size:14px;\">多项发明专利；已形成销售的产品十多个，如</span><span style=\"font-size:14px;\">PLC</span><span style=\"font-size:14px;\">型可调光衰减器等多个产品填补了国内空白，还有多个高端产品作为储备项目将适时向市场推广。</span><span></span><span style=\"font-size:14px;\">基于公司在本行业中的地位和领先优势，公司获得的荣誉有：国家高新技术企业、中国中小企业技术创新</span><span style=\"font-size:14px;\">100</span><span style=\"font-size:14px;\">强，四川省企业技术中心、四川省创新型企业、四川省战略性新兴产业重点企业等。</span><span></span></span>\r\n	</p>\r\n	<p class=\"MsoNormal\" style=\"text-indent:28.3pt;\">\r\n		<span style=\"font-size:14.0pt;font-family:&quot;\"><span style=\"font-size:14px;\">公司管理团队来自美国硅谷，核心成员都曾在世界知名企业担任高管。总裁李朝阳博士在</span><span style=\"font-size:14px;\">Intel</span><span style=\"font-size:14px;\">任职资深高级工程师长达</span><span style=\"font-size:14px;\">8</span><span style=\"font-size:14px;\">年时间，熟悉世界一流企业的管理模式和成功的企业文化；主管市场和主管技术的副总裁分别来自美国思科（</span><span style=\"font-size:14px;\">Cisco System</span><span style=\"font-size:14px;\">）和美国</span><span style=\"font-size:14px;\">DiCon</span><span style=\"font-size:14px;\">。此外，公司在美国硅谷还有顶尖的技术顾问团队。</span><span></span></span><span style=\"font-size:14.0pt;font-family:&quot;\"><span style=\"font-size:14px;\"><span style=\"font-size:14px;font-family:&quot;\">2011</span><span style=\"font-size:14.0pt;font-family:&quot;\"><span style=\"font-size:14px;\">年公司管理团入选四川省委组织部评选的首批“四川省顶尖团队”，</span><span style=\"font-size:14px;\">2012</span><span style=\"font-size:14px;\">年入选成都市委组织部评选的“成都市顶尖创新创业团队”。</span></span></span></span>\r\n	</p>\r\n	<p class=\"MsoNormal\" style=\"text-indent:28.3pt;\">\r\n		<span style=\"font-size:14.0pt;font-family:&quot;\"><span style=\"font-size:14px;\">\r\n		<p class=\"MsoNormal\" style=\"text-indent:28.3pt;\">\r\n			<span style=\"font-size:14.0pt;font-family:&quot;\"><span style=\"font-size:14px;\">公司位于成都双流的项目投产后，销售规模和利润迅猛增长，现已拥有</span><span style=\"font-size:14px;\">400</span><span style=\"font-size:14px;\">多名员工，年产值达</span><span style=\"font-size:14px;\">2</span><span style=\"font-size:14px;\">亿元，成为国内技术领先、规模领先的光通信器件制造商。</span><span style=\"font-size:14px;\">2013</span><span style=\"font-size:14px;\">年投资</span><span style=\"font-size:14px;\">1.8</span><span style=\"font-size:14px;\">亿元建成国内首个晶圆加工线，利用该加工平台，可以研发和制造光通信芯片产品和物联网的核心传感器，使公司可以横跨新一代信息技术和物联网两大战略性新兴领域。</span><span></span></span>\r\n		</p>\r\n</span></span><br />\r\n<span style=\"font-size:14.0pt;font-family:&quot;\"><span style=\"font-size:14px;\"><span style=\"font-size:14.0pt;font-family:&quot;\"><span style=\"font-size:14px;\"></span></span></span><span></span></span>\r\n	</p>\r\n</span></span>\r\n</p>', null, null, null, null, '0.00', '0.00', null, null, '0', null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null);
