INSERT INTO `biz_hospital` VALUES ('833db40d9a6c48d59eaca0ac64261b5d', '成都百隆家纺有限公司', '服装及其他纤维制造业', '否', '', '', '1993-05-06', '400', '董阿团', '董小勤', '18583602188', '13568808993', '成都市高新区中和公济桥路65号', '510122000047583', '', '', ' \r\n<p style=\"text-align:left;text-indent:32pt;\" class=\"MsoNormal\" align=\"left\">\r\n	<span style=\"font-family:仿宋_GB2312;font-size:16pt;\">成都百隆家纺有限公司创立于<span>1982</span>年，是一家专业设计、生产、销售寝室用品的企业，公司位于开发区二期，占地<span>15</span>亩，于<span>2010</span>年签约入园，<span>2011</span>年开工建设，<span>2012</span>年峻工投产。<span></span></span>\r\n</p>\r\n<p style=\"text-align:left;text-indent:32pt;\" class=\"MsoNormal\" align=\"left\">\r\n	<span style=\"font-family:仿宋_GB2312;font-size:16pt;\">公司拥有一批高素质的管理人才和技术人员，现代化园林式生产基地，电脑衔缝机、电脑绣花机、全自动装备及其它先进缝制设备<span>200</span>台，生产的“富美家”家庭床上用品系列，赢得了国内外消费者的青睐与赞美，随着公司的不断发展与扩张，产品主要外销至澳大利也、德国、东南亚等地。<span></span></span>\r\n</p>\r\n<p style=\"text-align:left;text-indent:32pt;\" class=\"MsoNormal\" align=\"left\">\r\n	<span style=\"font-family:仿宋_GB2312;font-size:16pt;\">卓尔不凡的设计和精湛的工艺打造出富美家产品高贵的气质，心灵手巧的完美为您编织一片彩艺动人的锦绣天堂，让您舒适生活每一天。<span></span></span>\r\n</p>', null, null, null, null, '0.00', '0.00', null, null, '0', null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null);
