INSERT INTO `biz_hospital` VALUES ('8fbc71449ed04f389eee5f2c26a15562', '成都迈斯拓新能源润滑材料股份有限公司', '石油化工', '否', '', '', '2012-03-23', '3350', '胡建伟', '吕丹', '13808092661', '85857666', '成都市双流县西南航空港经济开发区集中区内', '915101005920776418', '44', '若干', '<p style=\"text-align:left;color:#3D3D3D;text-indent:0px;background-color:#FFFFFF;\">\r\n	成都迈斯拓新能源润滑材料股份有限公司团队有着20余年的润滑油研发、 制造经验，已发展成为集研发、生产、销售和服务为一体的高新技术企业。是中国工程机械协会热载体加热技术委员会成员，中国锅炉水处理协会会员，四川标准化协会会员，四川石油协会会员。\r\n</p>\r\n<p style=\"text-align:left;color:#3D3D3D;text-indent:0px;background-color:#FFFFFF;\">\r\n	公司拥有多年从事导热油技术研究、生产和现场技术服务的专业队伍，与国内外多家科研院所保持长期的技术交流和项目合作，申请有多项导热油技术专利;研制出了永不结焦的长寿命导热油，该导热油可与国内外任何品牌的同型号导热油混用，并能显著减慢原导热油的结焦速率;发明的抗结焦剂、焦团分散剂成功应用到在用(报废)导热油在线再生技术中，该技术可不停产、不换油、不洗炉，在线再生后的导热油其使用性能可达到甚至超过原新导热油，该技术填补了国内外空白，达到了世界领先水平。\r\n</p>', null, null, null, null, '0.00', '0.00', null, null, '0', null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null);
