INSERT INTO `biz_hospital` VALUES ('9da1c73bb6394924bda9d69310d380c2', '成都博达爱福科技有限公司', '化工新材料', '是', '2013-11-18', '2016-11-17', '2010-07-30', '1967', '刘皓', '黄凤英', '13980606966', '85730469', '成都市双流县西南航空港经济开发区腾飞六路168号', '91510122558965098T', '21', '3个发明其中1个已授权，6个实用新型已授权', '<span> </span> \r\n<p style=\"text-indent:182.55pt;\" class=\"MsoNormal\">\r\n	<b><span style=\"background:white;line-height:150%;font-family:宋体;font-size:14pt;\">企业简介<span></span></span></b> \r\n</p>\r\n<span> </span> \r\n<p style=\"text-indent:27.85pt;\" class=\"MsoNormal\">\r\n	<span style=\"background:white;line-height:150%;font-family:宋体;font-size:14pt;\">成都博达爱福科技有限公司，是由成都晨光博达橡塑有限公司等股东投资成立的民营科技型公司，</span><span style=\"line-height:150%;font-family:宋体;font-size:14pt;\">坐落于成都天府新区核心位置双流西航港开发区腾飞六路</span><span style=\"line-height:150%;font-size:14pt;\"><span>168</span></span><span style=\"line-height:150%;font-family:宋体;font-size:14pt;\">号，占地</span><span style=\"line-height:150%;font-size:14pt;\"><span>38</span></span><span style=\"line-height:150%;font-family:宋体;font-size:14pt;\">亩，总投资</span><span style=\"line-height:150%;font-size:14pt;\"><span>1.2</span></span><span style=\"line-height:150%;font-family:宋体;font-size:14pt;\">亿元人民币，新建厂房</span><span style=\"line-height:150%;font-size:14pt;\"><span>10000</span></span><span style=\"line-height:150%;font-family:宋体;font-size:14pt;\">㎡。<span></span></span> \r\n</p>\r\n<span> </span> \r\n<p style=\"text-indent:27.85pt;\" class=\"MsoNormal\">\r\n	<span style=\"line-height:150%;font-family:宋体;font-size:14pt;\">公司于<span>2013</span>年被认定为国家级“高新技术企业”，<span>2014</span>年成都市新材料企业，<span>2013</span>年通过<span>ISO9001</span>：<span>2008</span>质量体系认证。<span>2014</span>年已申请<span>3</span>项发明专利和<span>1</span>项实用新型专利<span>,2016</span>年<span>1</span>月新增<span>5</span>项实用新型专利授权。<span></span></span> \r\n</p>\r\n<span> </span> \r\n<p style=\"text-indent:27.85pt;\" class=\"MsoNormal\">\r\n	<span style=\"line-height:150%;font-family:宋体;font-size:14pt;\">我公司是以生产太阳能电池背膜、核用润滑及高性能密封材料为主的国家级高新技术企业，主要产品有含氢硅油、液体硅橡胶、<span>MQ</span>硅树脂，产品广泛应用于新能源产业，电子信息产业芯片制造及核工业等行业。</span><span style=\"line-height:150%;font-family:宋体;font-size:14pt;\">其中我公司生产的高性能</span><span style=\"line-height:150%;font-family:宋体;font-size:14pt;\">密封材料广泛应用在</span><span style=\"line-height:150%;font-family:宋体;font-size:14pt;\">核工业、</span><span style=\"line-height:150%;font-family:宋体;font-size:14pt;\">汽车、航空航天、机械、石油、化工等行业的耐高温、耐特种化学介质的高性能密封材料，具有良好的物理和机械性能、回弹性高、压缩永久变形小、密封可靠、加工方便和使用寿命长。特别是</span><span style=\"line-height:150%;font-family:宋体;font-size:14pt;\">以全氟聚醚油的高性能高分子聚合物，有机氟的高端含氟精细化学品，尤其是含氟表面活性剂类产品为主导的，拥有自主知识产权的核心技术，以氟预混胶和氟混炼胶两大系列产品为重点的“橡胶王”产品</span><span style=\"line-height:150%;font-family:宋体;font-size:14pt;\">。</span><span style=\"line-height:150%;font-family:宋体;font-size:14pt;\">核用润滑系列产品拥有自主知识产权的高品质含氢硅油产品及重点发展特种改性硅油系列产品，加成型液体硅橡胶等前沿的高科技产品。广泛应用于航空航天、电子信息、电力电气、生物医药、医疗卫生、石油化工、农业水利、建筑工程、环境保护、纺织服装、新能源、汽车、钢铁、机械、食品、家装、日化和个人护理品等几乎所有的工业领域和高新技术领域，成为现代工业及日常生活不可或缺的新材料。</span><span style=\"line-height:150%;font-family:宋体;font-size:14pt;\"></span> \r\n</p>\r\n<span> </span> \r\n<p style=\"text-indent:28pt;\" class=\"MsoNormal\">\r\n	<span style=\"line-height:150%;font-family:宋体;font-size:14pt;\">我公司创新技术生产的产品，成本低廉，性能优异，质量稳定，有很强的竞争优势，不仅可以替代进口产品，而且有出口竞争力。其技术处于国际先进水平。</span><span style=\"line-height:150%;font-family:宋体;font-size:14pt;\">在行业中与美国<span>3M</span>，杜邦、日本大金等世界一流企业共同竞争，我们的产品已经远销至澳洲以外各大国际市场。除了做好精细化管理，我们更强调技术的投入对研发的支持，使核心技术和品质不断增强。目前我公司已取得“博达爱福”“<span>AIFLUO”</span>注册商标。<span></span></span> \r\n</p>\r\n<span> </span> \r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:14pt;\"><span><span> </span></span></span><span style=\"font-family:宋体;font-size:14pt;\">我们力求参与到全球化的合作中，与国际一流的企业合作，使之更加精细化、专业化、国际化。同时加大新材料的产业化、规模化，并计划在新津投资建设高端的具有国际竞争力的新材料基地，力争</span><span style=\"font-size:14pt;\"><span>3</span></span><span style=\"font-family:宋体;font-size:14pt;\">至</span><span style=\"font-size:14pt;\"><span>5</span></span><span style=\"font-family:宋体;font-size:14pt;\">年成为一家上市公司。</span><span style=\"font-size:14pt;\"></span> \r\n</p>\r\n<span> </span>', null, null, null, null, '0.00', '0.00', null, null, '0', null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null);
