INSERT INTO `biz_hospital` VALUES ('ced0d5ccf4f7432a94ce2aa4cd9c6908', '成都仁和制衣有限责任公司', '服装制造业', '否', '', '', '1998-5-20', '1510', '张屹', '', '13908205083', '028-85672061', '成都市双流县西航港经济开发区空港二路218号', '510122000027619', '120', '', '<span style=\"color:#000000;background-color:#FFFFFF;\"> 成都仁和制衣有限责任公司地处成都市双流县正兴镇。毗邻成仁路，人民南路延伸线，三环路，绕城高速，交通十分便利。</span>\r\n<p style=\"color:#000000;text-indent:0px;background-color:#FFFFFF;\">\r\n	 成都仁和制衣有限责任公司占地面积约30亩。现有生产线8条,平缝机和相关配套专用设备460余台，员工100余人，聘请中高级服装专业工程技术人员80名，进行服装设计和生产技术管理,具备年产100万套服装生产能力。\r\n</p>\r\n<p style=\"color:#000000;text-indent:0px;background-color:#FFFFFF;\">\r\n	 成都仁和制衣有限责任公司是国家卫生部卫生制服定点生产企业，是全国最大、最具专业性生产卫生制服的生产厂家之一。公司现已取得卫生部卫生制服定点生产资格、四川省、广西、河北、西藏等卫生制服定点生产资格、四川省盐务管理局盐政制服定点生产企业、中石油服装定点生产资格，并已通过ISO9001质量管理体系认证证书、ISO14001认证环境管理体系认证证书，其业务覆盖全国27个省（直辖市），企业信用等级为AAA级。公司现已形成五大主导产品开发体系：一、行政执法服类；二、卫生应急服、医院服装及布造用品类；三、西服；四、劳保服类；五、校服、军训服类.\r\n</p>', null, null, null, null, '0.00', '0.00', null, null, '0', null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null);
