INSERT INTO `product` VALUES ('b282717dda8445c7bea1e0e6684966bf', '9da1c73bb6394924bda9d69310d380c2', '无溶剂法甲基MQ硅树脂', 'MQ硅树脂Aifluo® MSR8001 外观为白色粉末或片状固体。该类树脂为甲基封端的多支链的低聚硅氧烷构成紧密的双层笼状结构。分子含有一定数量的活性硅羟基，在很多应用中能参与反应形成高度交联的稳定的聚硅氧烷网状结构。由于这种特殊的分子结构，MSR8001具有良好的机械性能、绝缘性、防潮防水性和耐高低温性能。 \nMSR8001为无溶剂法MQ硅树脂，不含对人体有害的异丙烷和二甲苯等有机溶剂。\n产品特点\nλ	对硅橡胶等有卓越的补强作用\nλ	与有机硅材料和有机载体良好兼容\nλ	固化后形成一层持久性膜\nλ	热稳定性达 300 ℃\nλ	良好的耐候性\nλ	良好的电气绝缘性能\nλ	良好的防潮拒水性能\nλ	溶于甲苯或二甲苯\n主要应用\nλ	用作有机硅压敏胶的补强填料\nλ	用作防刮、绝缘等防护性涂层的中间体 \nλ	用作脱模剂、消泡剂和防粘剂的剥离调节剂的添加剂\nλ	粉体材料的拒水性处理\nλ      可做彩妆，护肤品以及防晒霜的添加剂', 'http://106.75.132.101:8080/zqt/uploads/2016/08/30/f4d1d77e78754756814c83550677da4b.jpg', null, null);
