INSERT INTO `product` VALUES ('ecfa1bc34a7b463caf6b403090dec02d', '726f726e8c6d48689ed46b998d0f6e22', '微型封装CWDM模块', '飞阳科技的微型封装CWDM产品采用薄膜滤波片（TFF）技术和全胶微型光学封装工艺。该产品具有结构紧凑、通带内平坦、通道灵活配置、插入损耗低、隔离度高等特点，可广泛应用于CATV系统，城域网，OADM等领域。飞阳科技可根据客户特殊需求定制化生产。', 'http://106.75.132.101:8080/zqt/uploads/2016/08/29/eee593ca16314cb9b512cdbed47dfe2a.jpg', null, null);
