INSERT INTO `product` VALUES ('ef233e42ab394b8e993b19c598882dd8', '726f726e8c6d48689ed46b998d0f6e22', '气密封装保偏光纤阵列', '飞阳科技的气密封装用保偏光纤阵列产品是针对当今40G、100G高速相干接收系统而设计的。该产品可以把裸光纤通过气密性结构件进行封装，无需对光纤进行金属化，就可以通过常规的铅锡焊接的方式实现光电器件的气密性封装。', 'http://106.75.132.101:8080/zqt/uploads/2016/08/29/568a0413e2844a7581b8f9ea57e6ae6b.jpg', null, null);
